# 1. get the last letter in a wordvar1 = input('please enter your word: ')reduction_number = (len(var1) + 2) - (len(var1))lt_word = var1[len(var1) - reduction_number:]print('Last two word using normal method:', lt_word)# 2. Using function to solve 1 taskdef last_two(nam):    reduc_num = (len(nam) + 2) - (len(nam))    l_word = nam[len(nam) - reduc_num:]    return l_wordvar2 = input('please enter word:')print('Last two word using function:', last_two(var2))# 3. Using class functionclass Names:    def __init__(self, my):        self.my = my    @classmethod    def fetch_last_two(cls, my):        dn = (len(my) + 2) - (len(my))        result = my[len(my) - dn:]        return cls(result)var3 = input('please enter word:')print('Last two word using class method function:', Names.fetch_last_two(var3).my)